# Regular expression to find latitude and longitude in each span tag
pattern = re.compile(
    r'data-marker-latitude="([\d.]+)" data-marker-longitude="([\d.]+)"')

# Extracting latitude and longitude
locations = [{"lat": float(lat), "lng": float(lng)}
             for lat, lng in pattern.findall(html_data)]

# Display the locations array
print(locations)
